const burger=document.querySelector(".main__burger"),navigation=document.querySelector(".main-navigation");burger.addEventListener("click",(e=>{e.target.classList.contains("active")?(e.currentTarget.classList.remove("active"),navigation.classList.remove("active"),document.body.classList.remove("lock")):(e.currentTarget.classList.add("active"),navigation.classList.add("active"),document.body.classList.add("lock"))}));const swiper=new Swiper(".swiper",{direction:"vertical",loop:!0,pagination:{el:".swiper-pagination"}});